import { FC } from "react";

import { FileSearchOutlined, SendOutlined, ShoppingCartOutlined } from "@ant-design/icons";
import { Card, Tooltip, Image, Badge } from "antd";
import Link from "next/link";

import { useUserData } from "../../../context/UserContextProvider";
import { isProdEnv } from "../../../data/constant";
import { useWindowWidthAndHeight } from "../../../hooks";
import styles from "../../../styles/Wallet.module.css";
import { getExplorer } from "../../../utils/getExplorerByChain";
import { getBoostAttributes } from "../../../utils/getNftAttributes";
const { Meta } = Card;

type NftCardProps = {
    nft: Nft;
    index: number;
    handleTransfer: (nft: Nft) => void;
    handleDetail: (nft: Nft) => void;
    handleSelectNft?: (nft: Nft) => void;
    selectable: boolean;
};

const NftCard: FC<NftCardProps> = ({ nft, index, handleTransfer, handleDetail, handleSelectNft, selectable }) => {
    const { boostStatus } = useUserData();
    const { isMobile } = useWindowWidthAndHeight();

    const isUsedForBoost = (nft: Nft) => {
        const isSameContract = Number(nft.token_id) === Number(boostStatus?.tokenId);
        const isSameId = nft.token_address.toLowerCase() === boostStatus?.NftContractAddress.toLowerCase();
        return isSameContract && isSameId;
    };

    const description = !selectable ? (
        <span onClick={() => handleDetail(nft)}>Click for more details</span>
    ) : (
        <span>Boost: {getBoostAttributes(nft)}</span>
    );

    const showImage = (nft: Nft) => {
        return (
            <Image
                preview={false}
                src={nft?.image || "error"}
                fallback="data:image/png;base64,"
                alt="nft_image"
                width="100%"
                style={{
                    height: isMobile ? "129px" : "220px",
                    borderRadius: "10px",
                }}
                onClick={!selectable ? () => handleDetail(nft) : () => handleSelectNft!(nft)}
            />
        );
    };

    return (
        <Card
            hoverable
            actions={
                !selectable
                    ? [
                          <Tooltip title="View On Blockexplorer" key={index}>
                              <Link
                                  href={`${getExplorer()}token/${nft.token_address}?a=${nft.token_id}`}
                                  target="_blank"
                                  rel="noopener noreferrer"
                              >
                                  <FileSearchOutlined className={styles.nftActionIcon} />
                              </Link>
                          </Tooltip>,

                          <Tooltip title="Sell On Marketplace" key={index}>
                              <Link
                                  href={isProdEnv ? "https://opensea.io/" : "https://testnets.opensea.io/"}
                                  target="_blank"
                                  rel="noopener noreferrer"
                              >
                                  <ShoppingCartOutlined className={styles.nftActionIcon} />
                              </Link>
                          </Tooltip>,

                          <Tooltip title="Transfer NFT" key={index}>
                              <SendOutlined className={styles.nftActionIcon} onClick={() => handleTransfer(nft)} />
                          </Tooltip>,
                      ]
                    : undefined
            }
            style={{
                width: isMobile ? 160 : 210,
                height:
                    isMobile && !selectable ? 270 : isMobile && selectable ? 218 : !isMobile && !selectable ? 360 : 309,
                border: "2px solid #e7eaf3",
            }}
            cover={
                isUsedForBoost(nft) ? (
                    <Badge.Ribbon text="Used For Boost" color="orange">
                        {showImage(nft)}
                    </Badge.Ribbon>
                ) : (
                    <>{showImage(nft)}</>
                )
            }
            key={index}
        >
            <Meta className={styles.nftAttibutes} title={`${nft.name} #${nft?.token_id}`} description={description} />
        </Card>
    );
};

export default NftCard;
